	//normal kmer jump to normal kmer stay, step1, step2 and step3 
	long double stay = 0;
	long double step1 = 0;
	long double step2 = 0;
	long double step3 = 0;
	//normal kmer jump to bad kmer 
	long double jumpin = 0;
	//bad kmer jump to normal kmer
	long double jumpout = 0;
